mapper.update:
update tb_board
set board_count = board_count +1
where board_no = #{cnNo}

    	update tb_file
    	set file_name = #{renameFileName}
		where board_no = #{cnNo}

update tb_board
set board_title = #{cnTitle}, board_content = #{cnContent}
where board_no = #{cnNo}

update tb_club_notice
set club_Name = #{clubName}
where board_no = #{cnNo}

update tb_board
set board_Delete = 'Y'
where board_no = #{cnNo}

mapper.select:
select * from tb_club_notice join tb_board using(board_no) join tb_user using(user_id) where board_delete = 'N' and apt_name = #{aptName}
order by board_count desc
order by board_no desc

mapper.update:
update tb_comment
set REPLY_CONTENT = #{rContent}, COMMENT_DATE = SYSDATE
where REPLY_NO = #{rNo}

update tb_comment
set COMMENTS_DELETE = 'Y'
where REPLY_NO = #{rNo}
