mapper.update:
update tb_notice
set notice_count = notice_count +1
where notice_no = #{nNo}

    	update tb_file
    	set file_name = #{renameFileName}
		where notice_no = #{nNo}

update tb_notice
set notice_title = #{nTitle}, notice_content = #{nContent}, notice_dong = #{nDong}
where notice_no = #{nNo}

update tb_notice
set NOTICE_DELETE = 'Y'
where NOTICE_NO = #{nNo}

update tb_comment
set REPLY_CONTENT = #{rContent}, COMMENT_DATE = SYSDATE
where REPLY_NO = #{rNo}

update tb_comment
set COMMENTS_DELETE = 'Y'
where REPLY_NO = #{rNo}

mapper.select:
select apt_dong
from tb_apart
where apt_delete = 'N' and apt_name =#{aptName}

select * from tb_notice join tb_user using(user_id) where notice_delete = 'N' and apt_name = #{aptName}
order by notice_count desc
order by notice_no desc
